/* portal-design-templates */
@charset "utf-8";
.designportal .basic-heading1 {
  font-size: 2rem;
  font-weight: bold;
  margin-bottom: 15px;
}

@media screen and (max-width: 768px) {
  .designportal .basic-heading1 {
    margin: 0;
    padding: 0 15px 15px 15px;
  }
}

.designportal .basic-heading1--white {
  color: #FFFFFF;
}

.designportal .basic-heading2 {
  font-size: 1.5rem;
  font-weight: bold;
  margin: 20px 0 10px 0;
}

@media screen and (max-width: 768px) {
  .designportal .basic-heading2 {
    padding: 10px 0 10px 0;
    margin: 0;
  }
}

.designportal .basic-heading3 {
  margin: 20px 0 0 0;
  font-size: 1.1rem;
  font-weight: bold;
}

.designportal .basic-app-name {
  font-size: 0.9rem;
  font-weight: bold;
  margin-top: 10px;
  text-align: center;
}

@media screen and (max-width: 768px) {
  .designportal .basic-app-name {
    font-size: 1rem;
    text-align: left;
    margin-top: 0;
    margin-left: 10px;
  }
}

.designportal .basic-space-name {
  font-size: 0.9rem;
  margin-top: 10px;
}

@media screen and (max-width: 768px) {
  .designportal .basic-space-name {
    font-size: 1rem;
    text-align: left;
    margin-top: 0;
    margin-left: 10px;
  }
}

.designportal-basic {
  display: block;
  position: relative;
  background-color: #F2F2F2;
}

.designportal-basic h1, .designportal-basic h2, .designportal-basic h3, .designportal-basic ul, .designportal-basic ol, .designportal-basic p, .designportal-basic th, .designportal-basic td, .designportal-basic dl, .designportal-basic dt, .designportal-basic dd {
  margin: 0;
  padding: 0;
}

.designportal-basic ol {
  list-style: decimal;
}

.designportal .basic-heading1 {
  font-size: 2rem;
}

@media screen and (max-width: 768px) {
  .designportal .basic-heading1 {
    font-size: 2.4rem;
    padding-bottom: 5px;
  }
}

.designportal .basic-heading2 {
  font-size: 1.6rem;
}

@media screen and (max-width: 768px) {
  .designportal .basic-heading2 {
    font-size: 2rem;
  }
}

@media screen and (max-width: 768px) {
  .designportal .basic-heading3 {
    font-size: 1.6rem;
  }
}

.designportal .basic-text {
  font-size: 1rem;
}

@media screen and (max-width: 768px) {
  .designportal .basic-text {
    font-size: 1.6rem;
  }
}

.designportal .basic-list-item {
  font-size: 1rem;
}

@media screen and (max-width: 768px) {
  .designportal .basic-list-item {
    font-size: 1.6rem;
  }
}

.designportal .basic-container {
  width: 1080px;
  margin: 0 auto;
  padding: 20px 0 20px;
}

.designportal .basic-container:last-child {
  padding-bottom: 40px;
}

@media screen and (max-width: 1160px) {
  .designportal .basic-container {
    width: 96vw;
    margin: 0 2vw;
  }
}

@media screen and (max-width: 768px) {
  .designportal .basic-container {
    width: 100%;
    margin: 0 auto;
    padding: 20px 0 5px 0;
  }
}

.designportal .basic-appSet, .designportal .basic-spaceSet, .designportal .basic-dataSet {
  display: flex;
  flex-wrap: wrap;
  padding: 20px;
  background-color: #FFFFFF;
  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.05);
  border-radius: 10px;
  justify-content: center;
}

@media screen and (max-width: 768px) {
  .designportal .basic-appSet, .designportal .basic-spaceSet, .designportal .basic-dataSet {
    display: block;
    box-shadow: none;
    border-radius: 0;
    padding: 0;
  }
}

.designportal .basic-dataSet {
  justify-content: normal;
}

@media screen and (max-width: 768px) {
  .designportal .basic-dataSet {
    padding: 15px 15px;
  }
}

.designportal .basic-data {
  display: block;
  width: 100%;
  list-style-type: none;
  margin: 0 0 20px 0;
}

.designportal .basic-data-notifications {
  margin: 0 0 10px 0;
  text-align: left;
}

.designportal .basic-app {
  display: block;
  width: 168px;
  margin: 20px;
  list-style-type: none;
}

@media screen and (max-width: 1160px) {
  .designportal .basic-app {
    width: 16%;
    margin: 2%;
  }
  .designportal .basic-app:first-child {
    margin-left: 0;
  }
  .designportal .basic-app:last-child {
    margin-right: 0;
  }
}

@media screen and (max-width: 768px) {
  .designportal .basic-app {
    width: auto;
    margin: 0;
    padding: 10px 15px;
    border-bottom: solid 1px #dddddd;
  }
  .designportal .basic-app:last-child {
    border-bottom: none;
  }
}

.designportal .basic-app-link, .designportal .basic-space-link {
  display: block;
  color: #000000;
  background: #fff;
  text-decoration: none;
}

@media screen and (max-width: 768px) {
  .designportal .basic-app-link, .designportal .basic-space-link {
    display: flex;
    align-items: center;
  }
}

.designportal .basic-app-link:hover, .designportal .basic-space-link:hover, .designportal .basic-app-link:focus, .designportal .basic-space-link:focus {
  text-decoration: underline;
}

.designportal .basic-app-link:focus, .designportal .basic-space-link:focus {
  outline: #3498db solid 1px;
}

.designportal .basic-app-icon {
  width: 60px;
  height: 60px;
  margin: 0 auto;
  border-radius: 10px;
}

@media screen and (max-width: 768px) {
  .designportal .basic-app-icon {
    width: 60px;
    height: 60px;
    border-radius: 12px;
    margin: 0;
  }
}

.designportal .basic-app-icon-img {
  display: block;
  width: 50px;
  height: 50px;
  padding: 5px;
  fill: #FFFFFF;
}

@media screen and (max-width: 768px) {
  .designportal .basic-app-icon-img {
    width: 40px;
    height: 40px;
    padding: 5px;
  }
}

.designportal .basic-app-name {
  font-size: 1rem;
}

@media screen and (max-width: 768px) {
  .designportal .basic-app-name {
    font-size: 1.6rem;
  }
}

.designportal .basic-space {
  display: block;
  width: 220px;
  margin: 20px;
  list-style-type: none;
}

@media screen and (max-width: 1160px) {
  .designportal .basic-space {
    width: 46%;
    margin: 1.9%;
  }
}

@media screen and (max-width: 768px) {
  .designportal .basic-space {
    width: auto;
    margin: 0;
    padding: 10px 15px;
    border-bottom: solid 1px #dddddd;
  }
  .designportal .basic-space:last-child {
    border-bottom: none;
  }
}

.designportal .basic-space-img-container {
  height: 100px;
  border-radius: 10px;
}

@media screen and (max-width: 1160px) {
  .designportal .basic-space-img-container {
    width: 100%;
    height: 100px;
  }
}

@media screen and (max-width: 768px) {
  .designportal .basic-space-img-container {
    width: 40px;
    height: 40px;
    border-radius: 12px;
  }
}

.designportal .basic-space-img {
  display: block;
  height: 100px;
  border-radius: 10px;
  width: 100%;
  height: 100px;
  border-radius: 10px;
  background-size: cover;
  background-position: center;
}

@media screen and (max-width: 768px) {
  .designportal .basic-space-img {
    display: block;
    width: 40px;
    height: 40px;
  }
}

.designportal .basic-space-name {
  font-size: 1rem;
}

@media screen and (max-width: 768px) {
  .designportal .basic-space-name {
    font-size: 1.6rem;
  }
}

.designportal .basic-list {
  margin: 18px 0 18px 30px;
}

.designportal .basic-app-icon--red {
  background: linear-gradient(180deg, #fe5461 0%, #EF454A 100%);
}

.designportal .basic-app-icon--blue {
  background: linear-gradient(180deg, #56CCF2 0%, #2F80ED 100%);
}

.designportal .basic-app-icon--skyblue {
  background: linear-gradient(180deg, #a2dfe8 0%, #56CCF2 100%);
}

.designportal .basic-app-icon--pink {
  background: linear-gradient(180deg, #FF6EA2 0%, #E71F00 100%);
}

.designportal .basic-app-icon--green {
  background: linear-gradient(180deg, #6FCF97 0%, #219653 100%);
}

.designportal .basic-app-icon--purple {
  background: linear-gradient(180deg, #D16EFF 0%, #5300BD 100%);
}

.designportal .basic-app-icon--orange {
  background: linear-gradient(180deg, #F2C94C 0%, #EB5757 100%);
}

.designportal .basic-app-icon--yellow {
  background: linear-gradient(180deg, #ffd580 0%, #F2C94C 100%);
}

.boxContainer{
  font-size: 0;
}

.box{
  display: inline-block;
  font-size: 16px;
  font-weight: bold;
  color: #666;
  padding-left: 10%;
}

/*General settings
---------------------------------------------------------------------------*/
body {
	margin: 0px;
	padding: 0px;
	color: #666;	/*Overall font color*/
	font-size: 15px;	/*Font size*/
	line-height: 2;		/*Line height*/
}

h1,h2,h3,h4,h5,p,ul,ol,li,dl,dt,dd,form,figure,form {margin: 0px;padding: 0px;font-size: 100%;}
ul {list-style-type: none;}
ol {padding-left: 40px;padding-bottom: 15px;}
img,svg {border: none;max-width: 100%;height: auto;vertical-align: middle;}
table {border-collapse:collapse;font-size: 100%;border-spacing: 0;}
iframe {width: 100%;}

/*General link settings
---------------------------------------------------------------------------*/
a {
	color: #666;	/*Rich text color*/
	transition: 0.5s;	/*Mouseover timing*/
}
a:hover {
	color: #2799b5;			/*Mouseover font color*/
	text-decoration: none;	/*Delete underline on mouseover*/
}

/*Same for inner
---------------------------------------------------------------------------*/
.inner {
	margin: 0 auto;
	padding: 0 2%;		/*Box padding*/
}

/*Main menu
---------------------------------------------------------------------------*/
/*Menu block*/
#menubar {
    clear: both;overflow: hidden;
    background: #ffb0b0;	/*Background color (light pink) */
}
/*Settings for one menu*/
#menubar li {
	float: left;	/*Float left*/
	width: 16.66%;	/*Menu width（100÷6=16.66%）　If only 4　100÷4=25%*/
}
#menubar li a {
	display:  block;text-decoration: none;
    text-align: center;	/*Center text*/
    font-size: 20px;			/*Font size*/
	padding: 20px 0;	/*Menu padding*/
	color: #fff;	/*Font color*/
}
/*Additional settings for first menu*/
#menubar li:first-child a {
	border-left: 1px solid #000;	/*Menu border width, type, color*/
}
/*Decoration text settings*/
#menubar li a span {
	text-shadow: none;display: block;
	font-size: 20px;			/*Font size*/
	font-weight: normal;	/*Change font back to normal weight from bold*/
	letter-spacing: 0.2em;	/*Add letter spacing*/
	opacity: 0.5;			/*Make opacity 50%*/
}
/*Mouseover menu settings*/
#menubar li a:hover {
	background: #FFFFFF;	/*Background color*/
	color: #16a6b6; /*Font color*/
}

/*Don't show the mobile-version menu*/
#menubar-s {display: none;}
/*Don't show hamburger icon*/
#menubar_hdr {display: none;}

/*contents (block surrounding 3 columns)
---------------------------------------------------------------------------*/
#contents {
	clear: both;overflow: hidden;
	padding-top: 40px;		/*Box padding*/
    padding-bottom: 40px;	/*Box padding*/
    background: #f9d3cd;	/*Background color (light pink)*/
}

/*contents-in( (block surrounding sub and main) )
---------------------------------------------------------------------------*/
#contents-in {
	float: left;	/*float left*/
	width: 80%;		/*Block width*/
}

/*main contents (middle block)
---------------------------------------------------------------------------*/
#main {
	float: right;	/*float right*/
	width: 75%;		/*block width*/
}
/*main content h2 tag setting*/
#main h2 {
	text-align: center;	/*text center*/
	background-image: linear-gradient(45deg, #709dff 0%, #b0c9ff 100%);
	padding: 0.5em 1em;	/*all-around padding*/
	border-radius: 6px 6px 0px 0px;	/*border radius setting*/
    margin-bottom: 10px;
    font-weight: bold;
	color: #fff;	/*font color*/
}

/*sub contents (left block) 
---------------------------------------------------------------------------*/
#sub {
	float: left;	/*float left*/
	width: 22.5%;	/*block width*/
}

/*side content (left block) 
---------------------------------------------------------------------------*/
#side {
	float: right;	/*float right*/
	width: 18%;		/*block width*/
}

/*sub, side common settings
---------------------------------------------------------------------------*/
/*h2 header tag setting*/
#sub h2,
#side h2 {
	text-align: center;	/*text center*/
	background-image: linear-gradient(45deg, #709dff 0%, #b0c9ff 100%);
	padding: 0.5em 1em;	/* all-around padding*/
	border-radius: 6px 6px 0px 0px;	/*border radius setting*/
	margin-bottom: 10px;
    font-weight: bold;
    color: #fff;	/*font color*/
    min-width: 50%;
}

/*notice setting
---------------------------------------------------------------------------*/
/*notice setting*/
#sub .notice,
#side .notice {
	border: 1px solid #ccc;	/* border width, type, color*/
	padding: 10px;			/*box padding*/
    margin-bottom: 20px;	/*add bottom margin*/
    background: #fff;
}
/*h2 header in notice setting*/
#sub .notice h2,
#side .notice h2 {
	border-radius: 0;	/*border radius setting*/
}

/*sub, side recommended content 
---------------------------------------------------------------------------*/
/*settings for each box*/
#sub .list, #side .list {
	position: relative;overflow: hidden;
	font-size: 11px;	/*Font size*/
	line-height: 1.2;	/*Make line height smaller*/
	border-top: 1px solid #ccc;	/*top border width, type, color*/
    padding: 10px;	/*box padding*/
    background: #fff;
}
#sub .list a, #side .list a {
	text-decoration: none;display: block;overflow: hidden;
}
/*last box setting*/
#sub .list:last-of-type, #side .list:last-of-type {
	border-bottom: 1px solid #ccc;	/*bottom border width, type, color*/
	margin-bottom: 20px;	/*bottom margin*/
}
/*h4 tag setting*/
#sub .list h4, #side .list h4 {
  font-size: 15px;	/*Font size*/
	font-weight: bold;	/*Make bold*/
}
/*Setting for P tag*/
#sub .list p, #side .list p {
	padding: 0 !important;
}
/*Image setting in box*/
#sub .list figure img, #side .list figure img {
	float: left;	/*Float image left*/
    display: block;
    width: 50px;
    height: 50px;
    padding: 10px;
}

/*Setting for images less than 800px wide
------------------------------------------------------------------------------------------------------------------------------------------------------*/
@media screen and (max-width:800px){

    /*Box for smartphone menu*/
    #menubar-s {
        display: block;overflow: hidden;
        position: absolute;
        top: 100px;	/*Set 100px from top*/
        border-top: 1px solid #fff;		/*Top border width, type, color*/
        width: 100%;
        z-index: 10;
        animation-duration: 0.5S;	/*Animation timing*/
        animation-fill-mode: both;	/*Save last key frame of animation*/
    }
    /*Settings for each menu*/
    #menubar-s li a {
        display: block;text-decoration: none;
        padding: 15px 10px 15px 20px;	/*Padding settings*/
        border-bottom: 1px solid #fff;	/*Bottom border width, type, color*/
        background: #ffb0b0;	/*Background color (light pink)*/
    /* background: #dcf2e1;	Background color (green) */
        font-size: 20px;
        color: #fff;	/*Font color*/
    clear: both;overflow: hidden;
    }

    #menubar-s li a:hover {
        background: #FFFFFF;	/*Background color*/
        color: #16a6b6; /*Font color*/
    }

    /*Decoration text*/
    #menubar-s li a span {
        display: block;
        font-size: 12px;	/*Font size*/
    }
    /*Don't show desktop menu*/
    #menubar {display: none;}

    /*main,sub,side,contents-in
    ---------------------------------------------------------------------------*/
    #main, #sub, #side, #contents-in {
        float: none;
        width: auto;
    }
}

